import React from 'react';
import axios from 'axios';
import { useEffect, useState,useRef } from 'react';
import styled from 'styled-components';

function Contacts() {

  const [contacts, setContacts] = useState([]);
  const [currentChat, setCurrentChat] = useState([]);
  const [message, setMessage] = useState(null);
  const msg = useRef(null);
  useEffect(() => {
    axios.get("http://localhost:3001/getContacts").then((res) => {
      setContacts(res.data);
    })
  }, [])

  const sendMsg = (id) => {
    console.log(id)
  }

  function handleSetChat(id) {
    axios.get(`http://localhost:3001/getUsers/${id}`).then((res) => {
      setCurrentChat(res.data);
    })
  }
  return (
    <Container>
      <div className='items'>
        Click a user to chat
        {contacts.map((item, key) => (<div className='users' key={item.id} onClick={() => handleSetChat(item.id)}>
          {item.username}
          <br />
        </div>))}
      </div>
      <div className="chat">
        <h3>
          {currentChat.length == 1 ? <span>The chat</span> : null}
        </h3>
        {currentChat.map((item, key) => (<div className='usersChat' key={item.id}>
          {item.username}
          <br />
          <div className="row">
            <div className="col-12">
              <input type="text" className='form-control' value={msg} onChange={(e) => setMessage(e.target.value)} />
              <button className='btn btn-primary btn-sm w-100' onClick={() => sendMsg(item.id)}>Send Msg</button>

            </div>

          </div>
        </div>

        ))}


      </div>



    </Container>
  )
}
const Container = styled.div`
display:flex;
.items {
  width:50%;
  height:50%;
  .users {
    border:1px solid #dfdfdf;
    font-size:20px;
    padding:20px;
    margin:10px;
    cursor:pointer;
    
  }
}
.usersChat{
  font-size:50px;
}
`;

export default Contacts
