import React from 'react';
import styled from "styled-components";
import { useState } from 'react';
import { Link } from "react-router-dom";
import axios from 'axios';


function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');

  const data = {
    username,
    password,
    email
  }
  const handleSubmit = () => {
    Axios.post("http://localhost:3001/register", { username: username, email: email, password: password }).then(res => {
      console.log(res);
    })
  }

  return (
    <Container>
      <div className="my-5">
        <h4>REGISTER</h4>
        <div className=" ">
          <label htmlFor="Username"></label>
          <input type="text" onChange={(e) => setUsername(e.target.value)} placeholder='Username..' className="form-control" />
        </div>
        <div className=" ">
          <label htmlFor="Email"></label>
          <input type="text" onChange={(e) => setEmail(e.target.value)} placeholder='Email..' className="form-control" />
        </div>
        <div className="">
          <label htmlFor="Password"></label>
          <input type="password" onChange={(e) => setPassword(e.target.value)} placeholder='Password..' className="form-control" />
        </div>
        <div>
          <button type='submit' className='btn btn-success my-2' onClick={handleSubmit}>Sign Up</button>
        </div>
        <span className=''>Have an Account already? <Link to="/login">Log In</Link> </span>
      </div>
    </Container>
  )
}
const Container = styled.div`
  text-align:center;
  display:flex;
  justify-content:center;
  form {
      border:1px solid #dfdfdf;
      padding:100px;
      border-radius:20px;
  }
`;


export default Register
